    public function getSourceContext()
    {
        return new Twig_Source("{% extends 'base.html.twig' %}

{% block title %}All Articles{% endblock %}

{% block stylesheets %}{% endblock %}

{% block body %}

    <div class=\"example-wrapper\">
        {{ form_start(form) }}
        {{ form_row(form.title) }}
        {{ form_row(form.content) }}
        {{ form_row(form.category) }}
        <button class=\"btn\">{{ button_label|default('Create') }}</button>
        {{ form_end(form) }}

        <hr>
    </div>

    <h1>All articles from article's table : </h1>
    {% for article in articles %}
        <div>
            <h2>{{ loop.index }} / {{ article.title }} - Category : {{ article.category.name }}</h2>
            <p>{{ article.content }}</p>
        </div>
    {% endfor %}

    <a href=\"{{ path('blog_home') }}\">
        <p>Back to blog menu.</p>
    </a>

{% endblock %}", "blog/allArticles.html.twig", "/home/wilder/PhpstormProjects/SymfonyBlog/blog/templates/blog/allArticles.html.twig");
